package icehs.science.chapter04;

public class CalculatorTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 20;
		int num2 = 7;
		
		int plusNum = num1 + num2;
		int minusNum = num1 - num2;
		int multipleNum = num1 * num2;
		int divisionNum = num1 / num2;
		int remainderNum = num1 % num2;
		
		System.out.println("오 써쎀: " + plusNum);
		System.out.println("짋 써쎀: " + minusNum);
		System.out.println("썼 써쎀: " + multipleNum);
		System.out.println("씱얋 써쎀: " + divisionNum);
		System.out.println("씱쟵쵔: " + remainderNum);
		

	}

}
