package icehs.science.chapter05;

public class ConditionEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int age = 15;
		
		if (age >= 20) {
			System.out.println("입장하세요.");
		}
		if (age < 20) {
			System.out.println("입장 불가합니다.");
		}

	}

}
