package icehs.science.chapter05;

public class GradeIfElseTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int dongScore = 92;
		int soonScore = 85;
		
		if (dongScore >= 90) {
			System.out.println("턂쐕: A햬첎얦얯.");
		} else {
			System.out.println("턂쐕: A햬첇 쮅얫얦얯.");
		}
		
		if (soonScore >= 90) {
			System.out.println("턂쐕쩢: A햬첎얦얯.");
		} else {
			System.out.println("턂쐕쩢: A햬첇 쮅얫얦얯.");
		}

	}

}
